$(window).on("load", function () {
	ww = $(window).width();
	$(".slider .prev").hide();
	// if (ww <= 991) {
	// 	$(".stage").css("overflow", "hidden");
	// }

	// Slider 1 start
	item1 = $("#slider1 .item").outerWidth();
	console.log(item1);
	length = $("#slider1 .item").length;
	if (ww <= 767) {
		item1 = item1 * 1;
	} else {
		item1 = item1 * 1;
		// item1 = item1 * 2;
		console.log(item1);
	}
	if (length <= 1) {
		$("#slider1 > button").hide();
	}
	var slider1 = 0;
	$(document).on("click", "#slider1 .prev", function () {
		slider1 -= item1;
		console.log(slider1);
		$(this).parent("#slider1").find(".stage").animate({ scrollLeft: slider1 }, 500);
	});
	$(document).on("click", "#slider1 .next", function () {
		slider1 += item1;
		console.log(slider1);
		$(this).parent("#slider1").find(".stage").animate({ scrollLeft: slider1 }, 500);
	});
	// Slider 1 end

	// Slider 2 start
	item2 = $("#slider2 .item").outerWidth();
	length = $("#slider2 .item").length;
	if (ww <= 767) {
		item2 = item2 * 1;
	} else {
		item2 = item2 * 1;
		// item2 = item2 * 2;
	}
	if (length <= 1) {
		$("#slider2 > button").hide();
	}
	var slider2 = 0;
	$(document).on("click", "#slider2 .prev", function () {
		slider2 -= item2;
		$(this).parent("#slider2").find(".stage").animate({ scrollLeft: slider2 }, 500);
	});
	$(document).on("click", "#slider2 .next", function () {
		slider2 += item2;
		$(this).parent("#slider2").find(".stage").animate({ scrollLeft: slider2 }, 500);
	});
	// Slider 2 end

	// Slider 3 start
	item3 = $("#slider3 .item").outerWidth();
	length = $("#slider3 .item").length;
	if (ww <= 767) {
		item3 = item3 * 1;
	} else {
		item3 = item3 * 1;
		// item3 = item3 * 2;
	}
	if (length <= 1) {
		$("#slider3 > button").hide();
	}
	var slider3 = 0;
	$(document).on("click", "#slider3 .prev", function () {
		slider3 -= item3;
		$(this).parent("#slider3").find(".stage").animate({ scrollLeft: slider3 }, 500);
	});
	$(document).on("click", "#slider3 .next", function () {
		slider3 += item3;
		$(this).parent("#slider3").find(".stage").animate({ scrollLeft: slider3 }, 500);
	});
	// Slider 3 end

	// Slider 4 start
	item4 = $("#slider4 .item").outerWidth();
	length = $("#slider4 .item").length;
	if (ww <= 767) {
		item4 = item4 * 1;
	} else {
		item4 = item4 * 1;
		// item4 = item4 * 2;
	}
	if (length <= 1) {
		$("#slider4 > button").hide();
	}
	var slider4 = 0;
	$(document).on("click", "#slider4 .prev", function () {
		slider4 -= item4;
		$(this).parent("#slider4").find(".stage").animate({ scrollLeft: slider4 }, 500);
	});
	$(document).on("click", "#slider4 .next", function () {
		slider4 += item4;
		$(this).parent("#slider4").find(".stage").animate({ scrollLeft: slider4 }, 500);
	});
	// Slider 4 end

	// Slider footer start
	item5 = $("#slider5 .item").outerWidth();
	length = $("#slider5 .item").length;
	if (ww <= 767) {
		item4 = item4 * 1;
	} else {
		item4 = item4 * 1;
		// item4 = item4 * 2;
	}
	if (length <= 1) {
		$("#slider5 > button").hide();
	}
	var slider5 = 0;
	$(document).on("click", "#slider5 .prev", function () {
		slider5 -= item5;
		$(this).parent("#slider5").find(".stage").animate({ scrollLeft: slider5 }, 500);
	});
	$(document).on("click", "#slider5 .next", function () {
		slider5 += item5;
		$(this).parent("#slider5").find(".stage").animate({ scrollLeft: slider5 }, 500);
	});
	// Slider footer end

	$(".slider .stage").scroll(function () {
		var il = $(this).parents(".slider").find(".inside").position().left;
		var sl = $(this).parents(".slider").find(".stager").position().left;
		var sw = $(this).parents(".slider").find(".stager").width();
		var sr = ww - (sl + sw);
		if (il <= -10) {
			$(this).parents(".slider").find(".prev").show();
		} else {
			$(this).parents(".slider").find(".prev").hide();
		}
		if (sr <= -30) {
			$(this).parents(".slider").find(".next").show();
		} else {
			$(this).parents(".slider").find(".next").hide();
		}
	});
});
$(window).on("resize", function () {
	ww = $(window).width();
	item1 = $("#slider1 .item").outerWidth();
	item2 = $("#slider2 .item").outerWidth();
	item3 = $("#slider3 .item").outerWidth();
	item4 = $("#slider4 .item").outerWidth();
	item5 = $("#slider5 .item").outerWidth();
	if (ww <= 767) {
		item1 = item1 * 1;
		item2 = item2 * 1;
		item3 = item3 * 1;
		item4 = item4 * 1;
		item5 = item5 * 1;
	} else {
		item1 = item1 * 1;
		// item1 = item1 * 2;
		item2 = item2 * 1;
		// item2 = item2 * 2;
		item3 = item3 * 1;
		// item3 = item3 * 2;
		item4 = item4 * 1;
		// item4 = item4 * 2;
		item5 = item5 * 1;
		// item5 = item5 * 2;
	}
});
